import React, { useContext, useState } from "react";

export const SelectedAccountContext = React.createContext();

export const SelectedAccountProvider = ({ children }) => {
  const [account, setAccount] = useState();

  const updateAccount = (a) => {
    setAccount(a);
  };

  return (
    <SelectedAccountContext.Provider value={{ account, updateAccount }}>
      {children}
    </SelectedAccountContext.Provider>
  );
};

export const useAccount = () => {
  return useContext(SelectedAccountContext);
};
