import { Typography } from "@mui/material";
import React from "react";

const DeepStats = ({ selectedAccount, account }) => {
  if (selectedAccount)
    return (
      <div className="w-full bg-slate-50">
        <Card
          variant="outlined"
          style={{
            border: `2px solid ${orange[500]}`,
          }}
          className={`flex flex-col p-8 w-1/3 gap-6`}
        >
          <Typography variant="h6" color={orange[500]}>
            Securities balance
          </Typography>
          <div>
            <Typography variant="p" color="grey">
              Euro Balance
            </Typography>
            <Typography variant="h5" color="black" fontWeight={500}>
              {account.securitiesBalance["EUR"].toLocaleString("en-US", {
                style: "currency",
                currency: "EUR",
              })}
            </Typography>
          </div>
          <div>
            <Typography variant="p" color="grey">
              US Dollar Balance
            </Typography>
            <div className="flex justify-between items-end">
              <Typography variant="h5" color="black" fontWeight={500}>
                {account.securitiesBalance["USD"].toLocaleString("en-US", {
                  style: "currency",
                  currency: "USD",
                })}
              </Typography>
            </div>
          </div>
        </Card>
      </div>
    );
};

export default DeepStats;
