import React from "react";
import { Typography } from "@mui/material";
import { format } from "date-fns";

const Balance = ({ account, accountNumber }) => {
  return (
    <div
      style={{ border: "2px solid grey" }}
      className="flex flex-col p-8 w-1/3 gap-6 rounded-2xl"
    >
      <Typography variant="h6" color="grey">
        ACCOUNT {accountNumber}
      </Typography>
      <div>
        <Typography variant="p" color="grey">
          Euro Balance
        </Typography>
        <Typography variant="h5" color="black" fontWeight={500}>
          {account.balance["EUR"].toLocaleString("en-US", {
            style: "currency",
            currency: "EUR",
          })}
        </Typography>
      </div>
      <div>
        <Typography variant="p" color="grey">
          US Dollar Balance
        </Typography>
        <div className="flex justify-between items-end">
          <Typography variant="h5" color="black" fontWeight={500}>
            {account.balance["USD"].toLocaleString("en-US", {
              style: "currency",
              currency: "USD",
            })}
          </Typography>
          <Typography variant="p" color="black">
            {format(new Date(account.updatedAt), "d/MM/y")}
          </Typography>
        </div>
      </div>
    </div>
  );
};

export default Balance;
